mapper.update:
update comment
set weibo_id = #{weiboId,jdbcType=INTEGER},
  comment_text = #{commentText,jdbcType=VARCHAR},
  comment_create_time = #{commentCreateTime,jdbcType=TIMESTAMP},
  comment_user_id = #{commentUserId,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, weibo_id, comment_text, comment_create_time, comment_user_id
from comment
where id = #{id,jdbcType=INTEGER}
